import Navbar from "./components/navbar/navbar";
import UploadFile from "./components/fileupload";
import React, { lazy, Suspense, useState } from "react";
import './App.css';
import photos from "./components/data";
const Card = lazy(() => import('./components/card/card'));

function App() {
  const [items, setItems] = useState(photos);
  const [isCollaps, collapse] = useState(false);
  const toggle = () => collapse(!isCollaps);
  const [title, setTitle] = useState('Title')
  const [inputs, setInputs] = useState({ title: "", file: "", path: null })

  /* debouncing logic */
  const handleOnChange = (fun, timeout = 500) => {
    let timer = null;
    return (...args) => {
      clearTimeout(timer)
      timer = setTimeout(() => { fun.apply(this, args) }, timeout)
    }
  }

  const saveInput = (event) => {
    setTitle(event.target.value);
    setInputs({ title: event.target.value, file: event.target.files[0], path: URL.createObjectURL(event.target.files[0]) })
  }
  const handleSubmit = (event, data) => {
    event.preventDefault()
    console.log("data submit", event.target.value)
  }

  return (
    <React.Fragment>
      <Navbar />
      <div className="container text-center mt-5">
        <button className="btn btn-success float-end" onClick={toggle} >{isCollaps ? "Close" : "+Add"}</button>
        <div className="clearfix mb-4"></div>
        {!isCollaps && <UploadFile
          isVisible={isCollaps}
          handleOnChange={handleOnChange}
          handleSubmit={handleSubmit}
          saveInput={saveInput}
          title={title}
        />}
        <h1 className="mb-4">Gallery</h1>
        <Suspense fallback={<h4>Loading...</h4>}>
          <div className="row" >
            {items?.length && items?.map((item, index) => {
              return <Card key={index} src={item} />
            })}
          </div>
        </Suspense>
      </div>
    </React.Fragment>
  );
}

export default App;
