import React, { lazy, Suspense, useEffect, useState, useReducer } from "react";
import Navbar from "./components/navbar/navbar";
import UploadFile from "./components/fileupload";
import './App.css';
import photos from "./components/data";
const Card = lazy(() => import('./components/card/card'));

let initialState={
  items:[],
  isCollaps:false,
  inputs:{ title: "", file: "", path: '' },
}

const reducer=(state, action)=>{
 switch(action.type){
  case "SET_ITEM":
    return {...state, items:action.payload}
  case "SET_INPUT":
    return {...state, inputs:action.payload}
  case "IS_COLLAPS":
    return {...state, isCollaps:false}    
  default:
    return state  
 }
}


function App() {
  // const [items, setItems] = useState([]);// done
  const [isCollaps, collapse] = useState(false);
  const toggle = () => collapse(!isCollaps);
  // const [title, setTitle] = useState('Title')
  // const [inputs, setInputs] = useState({ title: "", file: "", path: null }) 
  const [count, setCount] = useState('');

  const [state, dispacth]=useReducer(reducer, initialState);

  console.log(state);

  useEffect(() => {
    // setItems(photos);
    dispacth({type:"SET_ITEM", payload:photos})
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [photos])

  useEffect(() => {
    setCount(`You have ${count} Image` + state?.items.length > 1 ? 's' : '')
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [state?.items])

  /* debouncing logic */
  const handleOnChange = (fun, timeout = 500) => {
    let timer = null;
    return (...args) => {
      clearTimeout(timer)
      timer = setTimeout(() => { fun.apply(this, args) }, timeout)
    }
  }

  const saveInput = (event) => {
    // setTitle(event?.target?.value);
    if (event.target.name === 'file') {
      // setInputs({ ...inputs, file: event?.target?.files[0], path: URL?.createObjectURL(event?.target?.files[0]) })
      dispacth({type:"SET_INPUT", payload:{...state.inputs, file: event?.target?.files[0], path: URL?.createObjectURL(event?.target?.files[0])}})
    } else{
      // setInputs({ ...inputs, title: event?.target?.value })
      dispacth({type:"SET_INPUT", payload:{ ...state.inputs, title: event?.target?.value }})
    }
  }
  const handleSubmit = (event) => {
    event.preventDefault()
    // setItems(() => [inputs?.path, ...items])
    dispacth({type:"SET_ITEM", payload:[state.inputs?.path, ...state?.items]})

    // setInputs({ title: "", file: "", path: null })
    dispacth({type:"SET_INPUT", payload:{title: "", file: "", path: '' }})
    collapse(false)
  }

  return (
    <React.Fragment>
      <Navbar />
      <div className="container text-center mt-5">
        <button className="btn btn-success float-end" onClick={toggle} >{isCollaps ? "Close" : "+Add"}</button>
        <div className="clearfix mb-4"></div>
        {!isCollaps && <UploadFile
          isVisible={isCollaps}
          inputs={state?.inputs}
          handleOnChange={handleOnChange}
          handleSubmit={handleSubmit}
          saveInput={saveInput}
          // title={title}
        />}
        <div className="mb-2">{count}</div>
        <h1 className="mb-4">Gallery</h1>
        <Suspense fallback={<h4>Loading...</h4>}>
          <div className="row" >
            {state?.items?.length && state?.items?.map((item, index) => {
              return <Card key={index} src={item} />
            })}
          </div>
        </Suspense>
      </div>
    </React.Fragment>
  );
}

export default App;
